void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvl = uv - vec2(0.01,0.01);



    vec3 col = vec3(sin(uv.x*97.+iTime*0.02),sin(uv.y*100.+iTime*0.6),0);

    col.y *= sin(length(uv*10.)+iTime*0.5);
    col.z = sin(uv.x*2. +uv.y*3. - iTime*0.17);
    col.y = sin(col.y*0.5 + uv.y*0.5+iTime*0.3);

    col.xyz *= (col.x+col.y+col.z);
    col = pow(col, vec3(2.,3.,2.));



    vec3 coll = vec3(sin(uvl.x*97.+iTime*0.5),sin(uvl.y*100.+iTime*6.),0);

    coll.y *= sin(length(uvl*10.)+iTime*1.5);
    coll.z = sin(uvl.x*2. +uvl.y*3. - iTime*0.7);
    coll.y = sin(coll.y*0.5 + uvl.y*0.5+iTime*1.);

    col = col*0.5 + coll*0.5;

    // Output to screen
    fragColor = vec4(col,1.0);
}
