void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime * 0.20;
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = uv;
    float d, a = atan(p.y, p.x) + time;
    d = sqrt(dot(p,p));
    p = vec2(cos(a), sin(a)) * d;
    vec4 col = texture(iChannel0, p + vec2(time * 0.01, 0.0));
    col.r = sin(col.r * 25.0 + time * 10.0);
    col.g = sin(col.g * 30.0 + time * 30.0);
    col.b = sin(col.b * 30.0 + time * 10.0);

    fragColor = col;
}
