vec4 comSq(vec4 n) {
    return vec4(n.x*n.x-n.y*n.y+n.z,2.*n.x*n.y+n.w,n.zw);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    fragColor = vec4(0,0,0,1);
    vec2 uv = fragCoord/iResolution.x*6.-iResolution.xy/iResolution.x*3.;
    vec4 n = vec4(0,0,uv);
    for (int i=0;i<100;i++)
        n = comSq(n);

    if (dot(n.xy,n.xy)<=4.)
        fragColor = vec4(1);
}
