// Smooth HSV2RGB from https://www.shadertoy.com/view/MsS3Wc
vec3 hsv2rgb(in vec3 c)
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	

	return c.z * mix( vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec4 color = vec4(0.5, 0.5, 0.5, 1.0);
    // Loop over all boids
    vec4 bcolor = vec4(0.0, 0.0, 0.0, 1.0);
    bool hit = false;
    for (int i = 0; i < NUM_BOIDS; i++) {
        ivec2 tex_pos = ivec2(i % int(iChannelResolution[0].x),
                                                i / int(iChannelResolution[0].x));
        vec4 pos_vel = texelFetch(iChannel0, tex_pos, 0);
        if (distance(fragCoord, pos_vel.xy) < BOIDS_SIZE) {
            vec3 rgb = hsv2rgb(vec3((float(i)+0.5) / float(NUM_BOIDS), 1.0, 0.9));
            bcolor += vec4(rgb, 1.0);
            hit = true;
        }
    }
    if (hit) {
        color = bcolor;
    }

    // Output to screen
    fragColor = color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    if (iFrame == 0) {
        // initialize the buffer
        vec4 pos_vel = texture(iChannel1, fragCoord/iChannelResolution[1].xy);
        pos_vel.xy = iResolution.xy / 2.0 + (pos_vel.xy - 0.5) * iResolution.xy * 0.2;
        pos_vel.zw = (pos_vel.zw - 0.5) * iResolution.xy * 1.0;
        fragColor = pos_vel;
        return;
    }
    ivec2 iFragCoord = ivec2(fragCoord);
    // 
    vec4 pos_vel = texelFetch(iChannel0, iFragCoord, 0);
    vec2 pos = pos_vel.xy;
    vec2 vel = pos_vel.zw;
    // ID of the current Boid
    int my_id = int(iFragCoord.y) * int(iResolution.x) + int(iFragCoord.x);

    // Implement Boids here
    float s_max = 10.0;
    float s_min = 5.0;
    float d_s = 30.0;
    float a_s = 0.02;
    float d_v = 150.0;
    float a_a = 0.03;
    float a_c = 0.001;
    float d_left = 200.0;
    float d_right = iResolution.x - float(200.0);
    float d_top = 160.0;
    float d_bottom = iResolution.y - float(200.0);
    float a_b = 0.5;

    vec2 sep = vec2(0.0);
    vec2 avg_pos = vec2(0.0);
    vec2 coh = vec2(0.0);
    vec2 avg_vel = vec2(0.0);
    vec2 align = vec2(0.0);
    vec2 boundary = vec2(0.0);

    // Count for avgs
    int count_s = 0; // Count for separation
    int count_c_a = 0; // Count for cohesion and alignment

    // Other boids loop
    for (int i = 0; i < NUM_BOIDS; i++){
        ivec2 otherPos = ivec2(i % int(iResolution.x), i / int(iResolution.y));
        vec4 other = texelFetch(iChannel0, otherPos, 0);
        float d = distance(pos, other.xy);

        // Separation
        if(d > 0.0 && d < d_s){
            vec2 diff = pos - other.xy;
            sep += diff;
            count_s++;
        }

        if(d > 0.0 && d < d_v){
            // Cohesion
            avg_pos += other.xy;
            // Alignment
            avg_vel += other.zw;

            count_c_a++;
        }

    }

    // Seperation avg
    if (count_s > 0){
        sep /= float(count_s);
    }

    // Cohesion and Aligment avg
    if (count_c_a > 0){
        avg_pos /= float(count_c_a);
        coh = avg_pos - pos;

        avg_vel /= float(count_c_a);
        align = avg_vel - vel;
    }

    // Boundary Force
    float turn_factor = 1.0;
    if (pos.x < d_left) boundary.x = turn_factor;
    else if (pos.x > d_right) boundary.x = -turn_factor;
    if (pos.y < d_top) boundary.y = turn_factor;
    else if (pos.y > d_bottom) boundary.y = -turn_factor;

    // Final force computation
    vec2 f = a_s*sep + a_c*coh + a_a*align + a_b*boundary;

    // Velocity update
    vel += f;

    // Max speed snippet from the PDF
    float vel_length = length(vel);
    if (vel_length > 0.0) {
        if (vel_length > s_max) {
            vel *= s_max / vel_length;
        } else if (vel_length < s_min) {
            vel *= s_min / vel_length;
        }
    }

    // Position update
    pos += vel;

    fragColor = vec4(pos, vel);
}
