void mainImage(out vec4 O, vec2 F) {
    vec2 r = iResolution.xy, u = (F+F-r)/r.y;    
    O.rgb*=0.;

    for (float i; i<20.; O.rgb +=
    15.50/((length(u*r.y)-i*500.9)+.1050)                   // shape distance
    * fract((tan(abs(i+vec3(.1,.055,1.59)))) )                           // color
    * abs(smoothstep(.35,.4,abs(abs(mod(iTime,.01)-i*1.1)+10.))) // animation
    ) u*=mat2(tan((iTime+i++)*.075 + ((abs(vec4(100,330,110,.0))))));   // rotation
}
