#define SCALE 2./exp(2.9*sin(iTime*0.3+71.1)+2.)
#define TRANSLATEX -1.+(2.5*(sin(5.+iTime*0.006)))
#define TRANSLATEY (cos(43.+iTime*0.08))/exp(2.9*sin(iTime*0.3+71.1)+2.)
#define SHOWGRID false
#define MAINITERATIONS 70.
#define PREITERATIONS 0.
#define PI asin(1.)*2.
vec2 c_mul(in vec2 z1, in vec2 z2){
return vec2(z1.x*z2.x-(z1.y*z2.y),z1.x*z2.y+(z1.y*z2.x));
}
vec2 c_cos(in vec2 z){
float ep=exp(z.y);
float em=1./ep;
return vec2(cos(z.x)*((ep+em)/2.),-sin(z.x)*((ep-em)/2.));
}
vec2 getnext(in vec2 z){
vec2 k=c_cos(z*PI);
return (c_mul((-5.)*k+vec2(7.,0.),z)+vec2(2.,0.)-(2.*k))/4.;
}
vec4 colorf(in float x){
return vec4((sin(x*2.)+1.)/3.,1./(log(x+2.)),0.8*(-1./(x+1.)+1.)+0.2,1.);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*2.-1.;
    uv*=SCALE;
    uv+=vec2(TRANSLATEX, TRANSLATEY);

   /*grid check*/
   if(SHOWGRID){
   if((uv.x-floor(uv.x))*iResolution.x<4.*SCALE){
    if((uv.x<0.5)&&(uv.x>-0.5)){
     fragColor = vec4(0.,1.,0.,1.);
    }else{
     fragColor = vec4(1.,0.,0.,1.);}
    return;
   }
   if((uv.y-floor(uv.y))*iResolution.y<4.*SCALE){
    if((uv.y<0.5)&&(uv.y>-0.5)){
     fragColor = vec4(0.,1.,0.,1.);
    }else{
     fragColor = vec4(1.,0.,0.,1.);}
    return;
   }
   }

    //uv.y=0.;
    //uv=floor(uv);


    /*main fractal*/
    float i=0.;
    while(i<PREITERATIONS){
     uv=getnext(uv);
     i++;
    }
    i=0.;
    bool b=true;
    while(i<MAINITERATIONS){
     vec2 k=uv-vec2(1.,0.);
     if(k.x*k.x+(k.y*k.y)<=0.25){
      if(SHOWGRID){
      fragColor=vec4(0.,0.,1.,1.);
      }else{
      fragColor=colorf(i);
      }
      b=false;
      return;
     }
     uv=getnext(uv);
     i++;
    }
    if(b){
    fragColor = vec4(0.,0.,0.,1.);}

}
