float sdHexagon( in vec2 p, in float r )
{
    const vec3 k = vec3(-0.866025404,0.5,0.577350269);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
    return length(p)*sign(p.y);
}

// [[0.744 0.244 0.577] [0.231 0.395 0.498] [0.759 1.375 0.932] [3.477 6.066 1.898]]
vec3 palette( in float t)
{
    vec3 a = vec3(0.5, 0.5, 0.5); //vec3(0.744, 0.244, 0.577);
    vec3 b = vec3(0.5, 0.5, 0.5); //vec3(0.231, 0.395, 0.498);
    vec3 c = vec3(1.0, 1.0, 1.0); //vec3(0.759, 1.375, 0.932);
    vec3 d = vec3(0.0, 0.333, 0.667); //vec3(3.477, 6.066, 1.898);
    return a + b*cos( 6.28318*(c*t+d) );
}

float saw( in float f, in float t )
{
    return abs((mod(t, f) - (f * 0.5)) * 2.0 * f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.x;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);
    for (float i = 0.0; i < 4.0; i++) {
        float x = (sin(iTime / 19.) * 0.5) + 1.75;
        uv = fract(uv * x) - 0.5;
        x = saw(1., iTime / 17.) * 0.8 + 0.1;
        float d = sdHexagon(uv, x) * exp(-length(uv0) * sin(iTime / 11.) * 2.);
        vec3 col = palette((length(uv0) / 4.) + (iTime / 3.) + (i / 3.));
        d = sin(d*6. + (iTime * 0.25)) / 6.;
        d = abs(d);
        d = pow(0.0025 / d, 1.5);
        finalColor += col * d;
    }
    fragColor = vec4(finalColor, 1.0);
}
