// From iq himself
vec3 palette(float t) {
    vec3 a = vec3(0.580, 0.468, 0.620);
    vec3 b = vec3(0.208, 0.403, 0.283);
    vec3 c = vec3(0.828, 0.578, 0.719);
    vec3 d = vec3(3.866, 3.440, 4.453);

    return a + b*cos( 6.28318*(c*t+d) );
}

float sdPentagon(in vec2 p, in float r) {
    const vec3 k = vec3(0.809016994,0.587785252,0.726542528);
    p.x = abs(p.x);
    p -= 2.0*min(dot(vec2(-k.x,k.y),p),0.0)*vec2(-k.x,k.y);
    p -= 2.0*min(dot(vec2( k.x,k.y),p),0.0)*vec2( k.x,k.y);
    p -= vec2(clamp(p.x,-r*k.z,r*k.z),r);    
    return length(p)*sign(p.y);
}

float sdHexagon(in vec2 p, in float r) {
    const vec3 k = vec3(-0.866025404,0.5,0.577350269);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
    return length(p)*sign(p.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    float d = sdPentagon(uv, sin(iTime * 0.5)) / sdHexagon(uv, cos(iTime * 0.5)) - 1.0;
    vec3 color = palette(d * sin(iTime));

    d = abs(cos(d * 80.0 + iTime) / 80.0);
    d = 0.003 / d;

    color *= d;

    fragColor = vec4(color, 0.0);
}
