vec3 palette(in float t)
{
//[[0.109 0.893 0.799] [0.922 0.109 0.187] [1.530 1.546 1.495] [3.525 3.377 5.319]]
    vec3 a = vec3(0.109, 0.893, 0.799);
    vec3 b = vec3(0.922, 0.109, 0.187);
    vec3 c = vec3(1.530, 1.546, 1.495);
    vec3 d = vec3(3.525, 3.377, 5.319);
    return a + b * cos(0.28318 * (c * t + d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for(float i = 0.0; i < 9.61; i += 0.17) {
        uv = fract(uv * 1.62) - 0.5;

        float d = length(uv) * exp(-length(uv0));

        vec3 color = palette(length(uv0) + -6.64*i + 0.295*iTime);

        d = sin(9.54*d + -0.057*iTime) / 0.83;
        d = abs(d);
        d = 0.00381 / d;

        finalColor += color * d;
    }

    fragColor = vec4(finalColor, 1.0);
}
