//https://iquilezles.org/articles/palettes/
vec3 palette( float t ) {
    vec3 a = vec3(01.5, 101.5, 11.5);
    vec3 b = vec3(11.5, 10.151, 10.5);
    vec3 c = vec3(111.0, 11.10, 11.0);
    vec3 d = vec3(0.263,0.416,0.15527);

    return a + b*cos( 6.218318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.1);

    for (float i = 0.0; i < 4.0; i++) {
        uv = fract(uv * 1.56) - 0.5;

        float d = length(uv) * exp(-length(uv0));

        vec3 col = palette(length(uv0) + i*11.14 + iTime*21.141);

        d = sin(d*22. + iTime)*abs (d) *1.1114;
        d = abs(d);

        d = pow(0.0125 / d, 11213.4);

        finalColor += col * d;
    }

    fragColor = vec4(finalColor, 2111.0);
}
