const float f = 10.0;
const float l = 1.0/f;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;
    vec2 uv_buff = fragCoord/iResolution.xy;
    vec2 pos_0 = iMouse.xy/iResolution.x;
    vec2 pos_1 = vec2(0.5,0.5)*iResolution.xy/iResolution.x;
    if(length(iMouse.zw - vec2(0.0,0.0))<40.0){
        float ang = iTime*1.5;
        float ring_amp = 25.0+sin(ang*3.0)*15.0;
        pos_0 = pos_1+(ring_amp*vec2(cos(ang), sin(ang)))/iResolution.x;
    }



    vec4 buffA = texture(iChannel0, uv_buff);

    vec4 col = vec4(0.0,0.0,0.0,1.0);

    col.r = ((fract(length(uv-pos_0)/l))+(fract(length(uv-pos_1)/l*1.01)))/2.0;

    if(col.r<0.03){
        fragColor=vec4(1.0,1.0,1.0,1.0);
    }else{
        fragColor.rgb = vec3(1.0,1.0,1.0)*(fract(length(uv-pos_0)/l))*0.1; 
    }
}
