float plot(vec2 st, float pct)
{
  return  smoothstep( pct-0.02, pct, st.y) -
          smoothstep( pct, pct+0.02, st.y);
}
float line(float x,float t)
{
    float value = mix(0.5,3.5,t);

    return 1.0 - pow(abs(x),value);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = 2.0 * fragCoord/iResolution.xy - 1.0;

    float t = (cos(iTime) + 1.0) * 0.5;

    float y = line(uv.x,1.0-t);

    uv.y = fragCoord.y/iResolution.y;

    float pct = plot(uv,y);

    fragColor = vec4(vec3(pct),1.0);
}
