#define tau 6.28318530
#define n1 0.28
#define n2 0.4
#define n3 1.3

// Function definitions
    float f(float x, float y, float t) {
        return n1*(0.*sin(1.*tau*x)+1.*sin(1.*tau*y)+1.*sin(0.1*tau*t)+1.*cos(1.*tau*x)+1.*cos(1.*tau*y)+1.*cos(0.1*tau*t))+0.1*t;
    }

    float g(float x, float y, float t) {
        return n2*(1.*sin(1.*tau*x)+1.*sin(1.*tau*y)+1.*sin(0.1*tau*t)+0.*cos(1.*tau*x)+1.*cos(0.*tau*y)+1.*cos(0.1*tau*t));
    }

    float h(float x, float y, float t) {
        return n3*(0.*sin(1.*tau*x)+1.*sin(1.*tau*y)+0.*sin(0.1*tau*t)+1.*cos(1.*tau*x)+1.*cos(1.5*tau*y)+1.*cos(0.1*tau*t));
    }

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = fragCoord / iResolution.xy;
    float t = iTime;

    // Iterations
    int n = 5;

    // Initial values
    float x = uv.x, y = uv.y;

    for(int i = 0; i < n; ++i) {
        float newX = f(x, y, t);
        float newY = g(x, y, t);
        t = h(x, y, t);
        x = newX;
        y = newY;
    }

    vec3 col = vec3(x, y, t);

    fragColor = vec4(col, 1.0);
}
