void mainImage( out vec4 o, vec2 u )
{
    float j = .8, 
          k = 1.5;

    vec2 n = iResolution.xy, 
         p = k*(u+u-n)/n.y;

    mat2 m = mat2(j, -1, 1, o = 0.*o + j);  

    for (o.z = k + p.y/4.; j < 1e2; j *= k)
        p = (k*p - .2*iTime - j) * m,
        n *= m,
        n += sin(p + n),
        o += o.zxyw*dot(cos(p + n), p/p)/9./j;

    o = 1.-exp(-4.*o*o);
}
