// variant of "Domain Rep in Polar Coords" by incre_ment. https://shadertoy.com/view/MXjGWD
// inspired by https://twitter.com/jcponcemath/status/1760814855324569854

void mainImage( out vec4 O, vec2 u )
{
   vec2  R = iResolution.xy, 
         U = ( u - .5* R ) / R.y;  
   float n = 32.,
         r = round(n*length(U)),
         d = 1.047/r;       // Pi/3

   O = .5 - .5* R.yyyy
              *(   length( U - r/n *cos( d * round( atan(U.y,U.x) / d ) +vec2(0,11)) )
                 - texture(iChannel0, u/R).r /70. // set your renderer or image here
                                       // fun without .r too. ;-)
               );
}
