// more readable version + comments  at the end
// extracted and golfed from "Shield emissive os" by oleg304. https://shadertoy.com/view/4XBGzG


// 219 chars: -5 by coyote, -5 by Fab

void mainImage( out vec4 o, vec2 p ){
    p += p - ( o.xyw = iResolution ).xy;                            // centering
    p /= .2 + .3* sqrt( o.w -= dot (p *= 2./o.x, p ) );             // sphere
    p.y += fract( ceil( p.x = p.x/.9 + iTime ) / 2. ) + iTime*.2,   // hexa: offset odd rows
    o =   vec4(2,3,5,1)/20. * o.w                                   // pseudo-shading
        / ( .1 + abs( max( p = abs(fract(p)-.5), p.x*1.5 ) + --p ).y ); // pattern: draw 2 lines * symmetries
}                       // ↑ tiling + symmetries





/** // 228 chars: -1 by iq, -5 by Greg

void mainImage( out vec4 o, vec2 p ){
    p += p - (o.xy = iResolution.xy);                               // centering
    p /= .2 + .3* sqrt( o.a = max( 1.- dot (p *= 2./o.x, p ), 0.) ),// sphere
    p.y += fract( ceil( p.x = p.x/.9 + iTime ) / 2. ) + iTime*.2,   // hexa: offset odd rows
    o =   vec4(2,3,5,1)/20. * o.a                                   // pseudo-shading
        / ( .1 + abs( max( p = abs(fract(p)-.5), p.x*1.5 ) + --p ).y ); // pattern: draw 2 lines * symmetries
}                       // ↑ tiling + symmetries

