/** // 234 chars, no variables

void mainImage( out vec4 o, vec2 p ){
    o.rgb = iResolution;
    p = 2.* ( p+p - o.xy ) / o.x ;                               // centered coords
    p /= .2 + .3* sqrt( o.a = max( 1.-dot(p,p), 0.) ),           // sphere. z = depth
    p.y += fract( ceil( p.x = p.x/.9 + iTime ) / 2. ) + iTime*.2,// hexa: offset odd rows
    p = abs( fract(p) - .5 ),                                    // tiling + symmetries
    o =   vec4(2,3,5,1)/20. * o.a                                // pseudo-shading
        / ( .1 + abs( max( p, p.x*1.5 ) + p - 1. ).y );          // pattern: draw 2 lines
}

