#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U)
vec4 hash44(vec4 p4)
{
    p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

Main {
    Q = A(U);
    vec4 m = vec4(0);
    for (float x = -2.; x <= 2.; x++) 
    for (float y = -2.; y <= 2.; y++) {
        m += A(U+vec2(x,y))/25.;
    }

    Q = mix(Q,m,vec4(.03,1,0,0));
    // Q.x = predator
    // Q.y = prey

    float r = .2*Q.x*Q.y;
    Q.x += r;
    Q.y -= r;

    Q.y += .001*(1.-Q.y);
    Q.x -= .001+.001*Q.x;
    Q.x = max(Q.x,0.);

    if (iFrame < 1) {
        Q.y = 1.;
        if (length(U-.5*R)<.2*R.y) Q.x = 1.;
    }

    if (iMouse.z>0.&&length(U-iMouse.xy)<20.)Q.x = 0.;

}

Main {

     Q = A(U);
     float dx = A(U+vec2(1,0)).x-A(U-vec2(1,0)).x;
     float dy = A(U+vec2(0,1)).x-A(U-vec2(0,1)).x;
     dx += A(U+vec2(1,0)).y-A(U-vec2(1,0)).y;
     dy += A(U+vec2(0,1)).y-A(U-vec2(0,1)).y;
     vec3 n = normalize(vec3(dx,dy,.1));
     vec2 h = -.5+hash44(vec4(iFrame,iTime,100,100)).xy;
     n = normalize(n+.4*(hash44(vec4(floor((U+h)*10.),20,10)).xyz*2.-1.));
     vec3 c = vec3(.5*R,-R.y);
     vec3 l = vec3(.7*R,-.5*R.y);
     vec3 m = vec3(U,0);
     float d = .5+.5*dot(reflect(normalize(c-m),n),normalize(l-m));
     Q = (.3+.7*sin(Q.x*vec4(1,2,3,4))+sin(Q.y*vec4(4,3,2,1)))*exp(-20.*d*d);
     Q = mix(B(U),Q,.03);
}

