vec3 print(vec2 xy, vec2 char, vec2 pos)
{
    float f = 10.0;
    vec3 charCol;
    if(abs((xy.x + 0.0312)-pos.x) < 0.06 && abs((xy.y + 0.02)-pos.y) < 0.06)
    {
        charCol = texture(iChannel0, (char / 16.0 + fract((pos / 16.0) + f * (xy - (pos / 16.0))) / 16.0)).rgb;
    }
    return charCol;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.y;
    float texCol;
    texCol += print(uv, vec2(8.0, 11.0), vec2(0.1, 0.5)).r;
    texCol += print(uv, vec2(5.0, 11.0), vec2(0.2, 0.5)).r;
    texCol += print(uv, vec2(12.0, 11.0), vec2(0.3, 0.5)).r;
    texCol += print(uv, vec2(12.0, 11.0), vec2(0.4, 0.5)).r;
    texCol += print(uv, vec2(15.0, 11.0), vec2(0.5, 0.5)).r;

    texCol += print(uv, vec2(7.0, 10.0), vec2(0.1, 0.4)).r;
    texCol += print(uv, vec2(15.0, 11.0), vec2(0.2, 0.4)).r;
    texCol += print(uv, vec2(2.0, 10.0), vec2(0.3, 0.4)).r;
    texCol += print(uv, vec2(12.0, 11.0), vec2(0.4, 0.4)).r;
    texCol += print(uv, vec2(4.0, 11.0), vec2(0.5, 0.4)).r;

    fragColor = vec4(vec3(texCol),1.0);
}
