void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;

    // 圆角半径
    float r = 0.1;

    // 计算屏幕宽高比
    float aspectRatio = iResolution.x / iResolution.y;

    // 映射 uv
    uv = abs(step(0.5, uv) - uv);

    //调整比例
    uv.x *= aspectRatio;

    if (uv.x < r && uv.y < r && length(uv - vec2(r)) > r)
    {
        float d = length(uv - vec2(r));
        fragColor = vec4(smoothstep(r, r + 0.01, d));
    }
    else
    {
        fragColor = vec4(0.0);
    }
}
