const vec3 berge = vec3(0.976, 0.949, 0.878);
const vec3 blue = vec3(0.0, 0.361, 0.6);
const vec3 yellow = vec3(1.0, 0.784,0.247);
const vec3 red = vec3(0.667, 0.133,0.141);

vec3 retancle(vec2 pos, float side0, float side1, vec2 uv) {
    vec2 bl = step(vec2(pos), uv);
    vec2 tr = step(vec2(1.0) - (vec2(side0, side1) + pos), 1.0 - uv);
    return vec3(bl.x * bl.y * tr.x * tr.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy / iResolution.xy;
    vec4 color = vec4(0.0);
    vec3 re[17];

    // column 0
    re[0] = retancle(vec2(0.0, 0.0), 0.1, 0.6, st) * berge;
    re[1] = retancle(vec2(0.0, 0.618), 0.04, 0.18, st) * red;
    re[2] = retancle(vec2(0.0, 0.82), 0.04, 0.18, st) * red;
    re[3] = retancle(vec2(0.05, 0.618), 0.05, 0.18, st) * red;
    re[4] = retancle(vec2(0.05, 0.82), 0.05, 0.18, st) * red;

    // column 1
    re[5] = retancle(vec2(0.11, 0.0), 0.25, 0.08, st) * berge;
    re[6] = retancle(vec2(0.11, 0.1), 0.25, 0.5, st) * berge;
    re[7] = retancle(vec2(0.11, 0.618), 0.25, 0.18, st) * berge;
    re[8] = retancle(vec2(0.11, 0.82), 0.25, 0.18, st) * berge;

    // column 2
    re[9] = retancle(vec2(0.37, 0.0), 0.1, 0.08, st) * blue;
    re[10] = retancle(vec2(0.37, 0.1), 0.1, 0.5, st) * berge;
    re[11] = retancle(vec2(0.37, 0.618), 0.1, 0.18, st) * berge;
    re[12] = retancle(vec2(0.37, 0.82), 0.1, 0.18, st) * berge;

    // column 3
    re[13] = retancle(vec2(0.48, 0.0), 0.02, 0.08, st) * blue;
    re[14] = retancle(vec2(0.48, 0.1), 0.02, 0.5, st) * berge;
    re[15] = retancle(vec2(0.48, 0.618), 0.02, 0.18, st) * yellow;
    re[16] = retancle(vec2(0.48, 0.82), 0.02, 0.18, st) * yellow;

    vec3 rr = vec3(0.0);
    for (int i=0; i<17; i++) {
        vec3 final = re[i];
        rr += final;
    }

    color = vec4(rr, 1.0);

    fragColor = color;
}
