float rectA(vec2 uv, float l, float b, float t, float r)
{
    return step(l, uv.x) * step(b, uv.y) * step(t, 1.0 - uv.y) * step(r, 1.0 - uv.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.0);

    float topRow1 = rectA(uv, 0.0, 0.9, 0.0, 0.7);
    float topRow2 = rectA(uv, 0.32, 0.5, 0.0, 0.1);

    col += mix(vec3(0.0, 0.0, 0.0), vec3(1.0, 0.0, 0.0), topRow1);
    col += mix(vec3(0.0, 0.0, 0.0), vec3(1.0, 1.0, 1.0), topRow2);

    fragColor = vec4(col,1.0);
}
