void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float phase = texture(iChannel0, uv).r * 3.14159 * 2.0;

    // Output to screen
    fragColor = vec4(sin(phase) * 0.5 + 0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv).rrrr;
    fragColor += texture(iChannel1, uv).rrrr;
    fragColor -= floor(fragColor);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 offs = vec2(1)/iResolution.xy;
    float t = texture(iChannel0, uv + offs * vec2(0,1)).r;
    float b = texture(iChannel0, uv + offs * vec2(0,-1)).r;
    float r = texture(iChannel0, uv + offs * vec2(1,0)).r;
    float l = texture(iChannel0, uv + offs * vec2(-1,0)).r;

    vec2 diff = vec2(t-b, r-l);

    fragColor = vec4(length(diff));
}
