vec3 spp = vec3(0);
vec2 march(in vec3 ro, in vec3 rd, inout float rl){
    float c = 0.;
    int m = 1;
    float pl = (-1. - ro.y)/rd.y;
    for(int i = 0;i < 64; i++){
        vec3 rp = ro +rl*rd;
        float sp = length(rp-spp) - 1.;
        float d = sp;
        rl += d;
        c = max(c,1./sp/5.);
        if(d > 10. || d < .001)break;
    }
    if(pl < rl && pl > 0.)rl = pl;
    return vec2(c,m);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;
    vec3 ro = vec3(0,0,4);
    spp.xz = vec2(sin(iTime),cos(iTime))*1.5;
    vec3 rd = normalize(vec3(uv,-1.));
    float rl = 0.;
    vec2 m = march(ro, rd, rl);
    vec3 rp = rl * rd + ro;
    vec3 rp2 = sign(sin(rp*2.));
    float co = 1.;
    if(rl > 100.)co = 0.;
    float c = m.x*2. + m.y*((max(rp2.x*rp2.z,0.))*co)/(length(rp-spp)*rl);

    fragColor = vec4(vec3(sqrt(c)),1.0);
}
