#define pal(p) (.6 + .5 * cos(3.14 * (h21(p) + vec4(0, .33, .66, 0))))
#define h21(a) fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123)

void mainImage(out vec4 o, vec2 u) {
    vec2 r = iResolution.xy;
         u = (u - r / 2.) / r.y;

    u = vec2(
            log(length(u)),
            atan(u.x, u.y) + iTime * .4
        ) * 2.;

    u += sin(
              (u.yx + tanh(cos(iTime * 1.5) * 5. - 4.)) * 3.

          ) * .1;

    vec2 id = floor(u),
          q = fract(u) - .5;

    o = max(.5 - length(q), 0.) * pal(id);
    o = pow(o, vec4(.35));
}
