float h21(vec2 p) {
 return fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453);
}

struct VP{
    vec2 id;
    float d;
};
vec2 rndPos(vec2 id){
    float s = 1.;
    //vec2 id = floor(p);

    float t = .3*iTime + 5132.213 * h21(id);
    return vec2(
                (cos(4.*t-100.)-cos(t)+sin(t*1.))/5.,
                (sin(t*4.) + cos(t-100.*21.))/5.
                );    
}

float circle(vec2 p, float r, float rs){
    return smoothstep(r+rs, r, length(p)); 
}
float len(vec2 p){
    float k = 3.;//10.*sin(20.*iTime/10.)+12.;
    return pow(pow(abs(p.x), k) + pow(abs(p.y), k), 1./k); 
}
VP voronoi(vec2 p){
    vec2 luv = fract(p)-.5;
    vec2 lid = floor(p);
    float d = 1e9;
    vec2 id=vec2(0);
    for(float i = -1.; i <= 1.; i+=1.){
        for(float j = -1.; j <= 1.; j+=1.){
            vec2 lp = vec2(i,j);
            float cd = len((lp+luv) - rndPos(lid-lp));
            if(cd < d){
                d = cd;
                id = lid - lp;

            }
            d = min(d,cd);
        }
    }
    return VP(id, d);
}

vec3 highContrast(vec3 col){
    vec3 f1 = col*col;
    vec3 f2 = 1.- ((col-1.)*(col-1.));
    return mix(f1, f2, col);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;
    float zoom =5.;
    uv.x += iTime *.2;
    uv.y += iTime *.05;
    uv = uv * zoom;
    vec2 lid = floor(uv);
    vec2 luv = fract(uv)-.5;

    vec3 col = vec3(0);
    vec2 p = luv - rndPos(lid);
    float c = circle(p, .01, .02);
    float gs = .49;

    col.r += h21(voronoi(uv).id+24.)*.8;
    col.b += h21(voronoi(uv).id + 91.)*.6;
    col.g += h21(voronoi(uv).id + 45.)*.6;

    col.r /= col.r;

    col.r *= .9;
    col = highContrast(col);
    //col *= vec3(.6, .2,.5);
    //col.gb += c;
    //if(abs(luv.x) > gs || abs(luv.y) > gs) col = vec3(1,0,0);

    fragColor = vec4((col),1.0);
}
