float plot(vec2 st, float y)
{
  return  smoothstep( y-0.02, y     , st.y) - 
          smoothstep( y     , y+0.02, st.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    vec2 m  = iMouse.xy / iResolution.xy;

    float edge1 = m.x;
    float edge2 = m.x+smoothstep(0.0,1.0,1.0-m.y);


    float y = smoothstep( edge1, edge2, st.x );

    // 渐变色 黑 黑渐变白 白 
    vec3 color = vec3(y);

    // 曲线
    float percent = plot(st,y);
    color = (1.0-percent)*color + percent*vec3(0.0,1.0,0.0);

    fragColor = vec4(color,1.0);
}
