void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float t = iTime;
	vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

	vec2 uv2 = t*0.5 + uv * 10.0;

	float ptn1 = (0.2 + .1 * (sin(uv2.x) * sin(uv2.y)));

	vec2 muv = vec2(fragCoord);

	float m = ((sin(uv2.x) * sin(uv2.y)) -sin(sin(uv.y-t*0.21)+uv.x*12.-t*2.)*0.5) * 2.;


    //float xor = mod(float(int(muv.y + muv.x)^int(muv.y - muv.x)), sin( (1.+(.02*m))*t * 0.001) * 5.0 + 5.0);
    float xor = mod(float(int(muv.y + muv.x)^int(muv.y - muv.x)), 70.+sin( (1.+(100.1*m))*(-cos(t*.126)*0.5+0.5)*.01) * 5.0 + 5.0);
	//float xor = mod(float(-int(muv.y + muv.x)|int(muv.y - muv.x)), sin( (100.+(.02*m))*t * 0.001) * 50.0 + 5.0);
	float ptn2 = (m)+(xor)-.8 < sin(t*.3)?xor:0.;

	float brd = 15.0;
	if ((fragCoord.x) > iResolution.x - brd||
	(fragCoord.x) < brd||
	fragCoord.y > iResolution.y - brd||
	fragCoord.y < brd) {
	brd = 1.;
    }else brd = 0.;

    float ptn3 = max(0.,round(0.6*sin(fract((.1*t)-uv.y*2.4)*fract((0.05*t)+uv.y*1.3)*20.)*mod((uv.y*100.+uv.x*200.),fract(sin(23.*t)*2.1))));

    float ptn4 = mod(0.2 * fragCoord.x * fragCoord.y, 1.0) > 0.5 ? 1.: 0.0;;

    vec2 ch_pos = (4. *uv)-vec2(-4.+fract(t*.1)*10.,0.0);
    float cherries = 1.-min(
        step(0.11, length(ch_pos-vec2(-0.09,-.12))),
        step(0.11, length(ch_pos-vec2(.09,-0.18)))
    );

    float refl = 1.-
        step(0.02, length(ch_pos-vec2(-0.09,-0.12)-vec2(-0.04,0.03)))*
        step(0.02, length(ch_pos-vec2(.09,-0.18)-vec2(-0.04,0.03)));

    float stems = 1.-max(
        step(0.1,abs(ch_pos.y-0.1)-0.1),
    min(
        step(0.01,length((ch_pos.y*.4)-ch_pos.x-.03+(sin(ch_pos.y*10.)*0.02))),
        step(0.01,length((ch_pos.y*-.1)-ch_pos.x+0.12+(sin(ch_pos.y*10.)*0.02)))
    ));
    vec3 ch = (stems*(1.-cherries)*vec3(0.196,0.612,0.122));
    ch += (cherries*vec3(0.749,0.075,0.000));
    ch = mix(ch, vec3(1.) ,.8*vec3(refl));

    float a=(t)*.3;
    mat2 rot = mat2(cos(a), sin(a), -sin(a), cos(a));

    uv-=vec2(0.4, -0.1);


    vec3 col = vec3(0.);
    col+= mix(vec3(0.282,0.396,0.851),0.5+.8*sin(t*.5)*vec3(0.667,0.710,0.800),(ptn1));
	col += (ptn2 * vec3(0.976,0.459,0.149) * .3);
    //col += (ptn3 * vec3(0.459,1.000,0.486) * .2);
    ch = mix(col,col*0.5+ch,max(stems,cherries));

    col = mix(col,ch,ch);
    col = mix(col,
        mix(vec3(0.820,0.761,0.694), vec3(0.522,0.490,0.667),vec3(ptn4)),
        vec3(brd));
    fragColor = vec4(vec3(col), 1.);
}
