#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)

#define r(a) mat2(cos(a + asin(vec4(0,1,-1,0))))

#define X(p) p *= r(round(atan(p.x, p.y) * 4.) / 4.)

void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,q,r=iResolution,
    d=normalize(vec3((C*2.-r.xy)/r.y,1));  
    for(float i=0.,a,s,e,g=0.;
        ++i<70.;
        O.xyz+=mix(vec3(1),H(g*.1),.8)*3./e/8e3
    )
    {
        p=g*d;
        p.z+=iTime;
       p.yx*=mat2(cos(iTime*0.01),sin(iTime*0.01),-sin(iTime*0.01), cos(iTime*0.01));
           X(p.yx);
            X(p.zy);
        a=35.;
        p=mod(p-a,a*2.)-a;
        s=2.;
        for(int i=0;i++<8;){
            p=.3-abs(p);
            p.x<p.z?p=p.zyx:p;
            p.z<p.y?p=p.xzy:p;
            s*=e=1.5+sin(iTime*0.01)*.1;
            p=abs(p)*e-
                vec3(
                    5.+sin(iTime*.03+.5)*3.,
                   70,
                    8.+cos(iTime*.5)*5.
                 );
         }
         g+=e=length(p.yyzz)/s;
    }
}
