//#define f(v)  cos( 20.*(v) )
  #define f(v) max( cos( 20.*(v) ) , 0. )

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U = ( 2.*u - R ) / R.y,                // normalized coordinates
          M = iMouse.xy;
    float l = length(U) *exp2(-iTime),           // polar coordinates
          L = log(l),
          a = atan(U.y,U.x), 
          k = length(M)<20. ? 1. : 2.*M.x/R.x,   // slope = Mouse.x
          A = a - L * k,                         // logarithmic spiral u
          B = a + L / k;                         // logarithmic spiral v

 // O = vec4(  cos( 10.*(a-6.28*l)) ,  cos( 40.*(a-1./(6.28*l))), 0,0); // Archimede
    O = f( vec4( A, B, (A-B)/2., (A+B)/2. ) ); O =  max( O , O.a );     // Logarithmic

 // O = 1. - (1.-O) / fwidth(O);                 // 1-pixel lines
 // O = 1. - (1.-2./R.y - O) / fwidth(O);        // variant: better AA
    O = 1. - (1.-O) *.5/ fwidth(A+B);            // variant: better AA
 // O /= fwidth(O);                              // variant: strips
 // O = 1. - (.5-O) / fwidth(O);                 // variant: strips
 // O = 1. - abs(.5-O) / fwidth(O);              // variant
}
