// #define hue(v)  ( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) )
// #define hue(v)  ( .5 +      cos( 6.3*(v)  + vec4(0,23,21,0)  ) )
// #define hsv(c)  ( (c).z * mix( vec3(1), ( .6 + .6 * cos( 6.3*(c).x  + vec3(0,23,21)  ) ), (c).y ) )
// #define hsv(c)  ( (c).z * ( 1. + (c).y* ( .6 * cos( 6.3*(c).x  + vec3(0,23,21)  ) -.4 ) ) ) // golfed

vec4 hue(float h){
    return .6+.6*cos(6.3*h+vec4(0,23,21,0));
  //return .5+   cos(6.3*h+vec4(0,23,21,0)); // full saturated hue
}

void mainImage(out vec4 O,vec2 U){
    float h=(length(U+U-iResolution.xy)/iResolution.y)-iTime*0.25;
    O=hue(h);
}
