float sdBox(vec2 p){
    vec2 q = abs(p - .5) - .48;
    return length(max(q,.0)) + min(max(q.x,q.y),.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 R = iResolution;
    vec2 uv = (2.*fragCoord -R.xy)/R.y;

    vec4 back = fragColor = vec4(.1*(uv.y+1.5)*(uv.x+2.));

    uv   *= 3.;
    uv.x *= .85;

    float k = .5 * sqrt(4. - uv.x*uv.x), dx, y, dist,
          aa = 6./R.y, t = fract(.6*iTime);

    if(abs(uv.x) > 2.) 
        return;

    if(abs(uv.x) > .3)
        uv.x *= .5*abs(uv.x);

    for(float i=0.; i<2.; i++){
        dx = floor(uv.x+4.)/8.;

        y = uv.y + t + step(t,dx) - k;

        y = y > 0. ? mod(y - 4.*fract(dx-t), 5.) : fract(y);

        vec2 p = vec2(fract(uv.x), y);

        dist = smoothstep(-aa, aa, sdBox(p));
        fragColor = mix(vec4(i*.25)+back-.13, fragColor ,dist);

        k = 1.1 - k;
        uv.x = 3. - uv.x;
    }
}
