// Optimized Camo-style L-System Shader
#define LAYERS 5
#define ITERATIONS 3

float hash(vec2 p) {
    p = fract(p * vec2(123.34, 456.21));
    p += dot(p, p + 400.32);
    return fract(p.x * p.y);
}

float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    f = f * f * (30.0 - 2.0 * f);
    return mix(mix(hash(i), hash(i + vec2(1.0, 0.0)), f.x),
               mix(hash(i + vec2(0.0, 1.0)), hash(i + vec2(1.0, 1.0)), f.x), f.y);
}

float fbm(vec2 p) {
    float v = 0.0;
    float a = 0.5;
    for (int i = 0; i < 3; i++) {
        v += a * noise(p);
        p *= 10.0;
        a *= .1;
    }
    return v;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 p = (.010 * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);

    vec3 col = vec3(0.0);
    float time = iTime * 10.2;

    for (int layer = 0; layer < LAYERS; layer++) {
        float layerTime = time + float(layer) * 2.3;
        vec2 offset = vec2(sin(layerTime * 0.1), cos(layerTime * 0.2)) * 0.3;

        for (int i = 0; i < ITERATIONS; i++) {
            float scale = 3.0 + float(i) * 2.0 + sin(layerTime) * 0.5;
            vec2 q = p * scale + offset;

            float angle = layerTime * 0.1 + float(i) * 1.5;
            q = mat2(cos(angle), -sin(angle), sin(angle), cos(angle)) * q;

            float pattern = fbm(q + fbm(q + fbm(q)));

            vec3 layerColor = mix(
                vec3(0.498,0.102,0.102),  // Dark green
                vec3(0.380,0.200,0.800),  // Light tan
                float(layer) / float(LAYERS - 1)
            );

            col += layerColor * smoothstep(0.3, 0.7, pattern) * (1.0 - float(i) / float(ITERATIONS));
        }
    }

    col /= float(LAYERS);
    col = mix(col, vec3(fbm(p * 10.0 + time)), 0.1);  // Add fine grain

    // Contrast and brightness adjustment
    col = pow(col, vec3(0.8));
    col = col / (1.0 + col);

    fragColor = vec4(col, 1.0);
}
