#define PI (3.1415926535897932384)
#define TAU (2.0 * PI)

#define GAMMA (2.2)
#define COLORS (2.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = texture(iChannel0, uv);

    vec4 dither = (
        texture(
            iChannel1, 
            fragCoord.xy / iChannelResolution[1].xy
        ).xxxx
    );

    float phi = (sqrt(5.0) - 1.0) / 2.0;

    dither += fract(phi * float(iFrame));
    dither = acos(cos(TAU * dither)) / PI;

    fragColor = pow(
        round(
            (COLORS - 1.0) * pow(fragColor, vec4(GAMMA))
            + dither - 0.5
        ) / (COLORS - 1.0),
        vec4(1.0 / GAMMA)
    );

    if (uv.y <= 0.1) {
        fragColor = dither;
    }
}
