void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Размер квадрата
    float squareSize = 50.0;

    // Количество квадратов по горизонтали и вертикали
    vec2 numSquares = vec2(iResolution.x / squareSize, iResolution.y / squareSize);

    // Определение индекса квадрата для текущего пикселя
    vec2 squareIndex = floor(fragCoord / squareSize);

    // Вычисление новых координат внутри каждого квадрата без изменения положения пикселей
    vec2 newCoord = mod(fragCoord, squareSize);

    // Перемешивание индексов квадратов (можно изменить логику перемешивания по своему усмотрению)
    vec2 newSquareIndex = vec2(mod(squareIndex.y, numSquares.y), mod(squareIndex.x, numSquares.x));

    // Новые координаты пикселя после перемешивания
    vec2 finalCoord = newSquareIndex * squareSize + newCoord;

    // Получение цвета из исходного изображения
    fragColor = texture(iChannel0, finalCoord / iResolution.xy);
}
