#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01

float GetDist(vec3 p) {
	vec4 s = vec4(0, 1, 6, 1);

    float sphereDist =  length(p-s.xyz)-s.w;
    float planeDist = p.y;

    float d = min(sphereDist, planeDist);
    return d;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

// Keep GetLight focused on calculating light intensity
float GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 5, 6);
    lightPos.xz += vec2(sin(iTime), cos(iTime)) * 2.;
    vec3 l = normalize(lightPos - p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p + n * SURF_DIST * 2., l);
    if (d < length(lightPos - p)) dif *= .1;

    return dif;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;
    vec3 ro = vec3(0, 1, 0);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    float d = RayMarch(ro, rd);
    vec3 p = ro + rd * d;

    vec3 col;
    float lightIntensity = GetLight(p);

    // Psychedelic color base
    vec3 psychedelicBase = vec3(sin(iTime), cos(iTime), sin(iTime + cos(iTime)));

    if (d < MAX_DIST) {
        if (GetDist(p) < 0.1) { // Sphere
            // Sphere: More complex, morphing psychedelic colors
            col = vec3(
                0.5 + 0.5 * sin(iTime * 1.5 + length(p) + sin(iTime)),
                0.5 + 0.5 * cos(iTime * 1.7 + length(p) + cos(iTime)),
                0.5 + 0.5 * sin(iTime * 1.3 + length(p))
            ) * lightIntensity;
        } else { // Floor
            // Floor: Distinct yet trippy and dynamic colors
            col = vec3(
                0.5 + 0.5 * sin(p.x * 2.0 + iTime * 2.0),
                0.5 + 0.5 * sin(p.y * 2.0 + iTime * 2.0 + 2.0),
                0.5 + 0.5 * sin(p.z * 2.0 + iTime * 2.0 + 4.0)
            ) * lightIntensity;
        }
    } else {
        col = vec3(0.0); // Background color
    }

    // Gamma correction
    col = pow(col, vec3(0.4545));
    fragColor = vec4(col, 1.0);
}
