void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float col = 0.0;
    float col2 = 0.0;

    col += sin(uv.x * 6. + sin(iTime + uv.y * 40. + cos(uv.x * 30. + iTime * 2.))) * 0.5;
    col2 += cos(uv.x * 6. + cos(iTime + uv.y * 40. + sin(uv.x * 30. + iTime * 2.))) * 0.5;

    // Output to screen
    fragColor = vec4(col + uv.x, col2 + uv.x, col * col2,1.0);
}
