void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalize pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Create a wave effect that starts from the bottom
    float waveFrequency = 10.0; // Frequency of the waves
    float waveSpeed = 3.0; // Speed of the wave movement
    float waveAmplitude = 0.05; // Amplitude of the waves

    // Calculate the wave height using sine function
    float wave = sin(waveFrequency * uv.x + iTime * waveSpeed) * waveAmplitude; // Wave position

    // Calculate the Y position of the milk (offset from the bottom)
    float milkY = wave + 0.15; // Adjust height to be visible above the bottom

    // Milk color: a solid cartoonish light milky white
    vec3 milkColor = vec3(1.0, 0.95, 0.9); // Solid color

    // Create alpha based on the milk's Y position
    float alpha = smoothstep(0.0, 0.05, milkY - uv.y); // Control visibility of the milk

    // Final color output
    vec3 finalColor = milkColor * alpha; // No background color, keep the milk color

    // Output the final color with the alpha value
    fragColor = vec4(finalColor, alpha);
}
