#ifdef GL_FRAGMENT_PRECISION_HIGH
precision highp float;
#else
precision mediump float;
#endif

#ifndef PI
#define PI 3.1415927
#endif

vec2 rot2d(vec2 uv, vec2 pivot, float angle)
{
	return vec2(	cos(angle) * (uv.x - pivot.x) + sin(angle) * (uv.y - pivot.y) + pivot.x,
			cos(angle) * (uv.y - pivot.y) - sin(angle) * (uv.x - pivot.x) + pivot.y);
}

float sdRoundedBox(in vec2 uv, in vec2 pos, in vec2 size, in float r)
{
	vec2 d = abs(uv-pos)-size + r;
	return length(max(d, vec2(0.0))) + min(max(d.x, d.y), 0.0) - r;
}

float sdBox(in vec2 uv, in vec2 pos, in vec2 size)
{
	vec2 d = abs(uv-pos)-size;
	return length(max(d, vec2(0.0))) + min(max(d.x, d.y), 0.0);
}

float sdDisk(vec2 uv, vec2 pos, float r)
{
	return length(uv-pos) - (r*0.5);
}

// Adapted from https://www.shadertoy.com/view/DsccDH
float sdRing(in vec2 uv, in vec2 pos, in float a, in float g, in vec2 r)
{
	uv -= pos;

	uv = rot2d(uv, vec2(0.0), a);

	vec2 cs = vec2(cos(g*PI),sin(g*PI));

	uv.x = abs(uv.x);

	uv = mat2(cs.x, cs.y, -cs.y, cs.x) * uv;

	return max(	abs(length(uv)-r.x)-r.y*0.5,
			length(vec2(uv.x,max(0.0,abs(r.x-uv.y)-r.y*0.5)))*sign(uv.x) );
}

float numbers(vec2 uv, vec2 pos, float blurval, int id)
{
	float d = 1.0;

	if(id == 0)
	{
		d = sdRing(uv, pos+vec2(0.0, 0.225), PI, -0.5, vec2(0.225, 0.05));
		d = min(d, sdBox(uv, pos-vec2(0.225,0.0), vec2(0.025, 0.225)));
		d = min(d, sdBox(uv, pos+vec2(0.225,0.0), vec2(0.025, 0.225)));
		d = min(d, sdRing(uv, pos-vec2(0.0, 0.225), PI, 0.5, vec2(0.225, 0.05)));
	}
	else if(id == 1)
	{
		d = sdRing(uv, pos+vec2(0.0, 0.225), PI*0.25, 0.25, vec2(0.225, 0.05));
		d = min(d, sdBox(uv, pos, vec2(0.03, 0.474)));
	}
	else if(id == 2)
	{
		d = sdRing(uv, pos+vec2(0.0, 0.225), -PI*0.25, 0.25, vec2(0.225, 0.05));
		d = min(d, sdBox(uv, pos+vec2(0.225, 0.11), vec2(0.025, 0.12)));
		d = min(d, sdBox(uv, pos-vec2(0.0), vec2(0.25, 0.025)));
		d = min(d, sdBox(uv, pos-vec2(0.225, 0.22), vec2(0.025, 0.225)));
		d = min(d, sdBox(uv, pos-vec2(0.1, 0.45), vec2(0.15, 0.025)));
	}
	else if(id == 3)
	{
		d = sdRing(uv, pos+vec2(0.0, 0.225), -PI*0.5, 0.5, vec2(0.225, 0.05));
		d = min(d, sdRing(uv, pos-vec2(0.0, 0.225), -PI*0.75, 0.75, vec2(0.225, 0.05)));
	}
	else if(id == 4)
	{
		d = sdRing(uv, pos+vec2(0.0, 0.225), PI*0.25, 0.25, vec2(0.225, 0.05));
		d = min(d, sdBox(uv, pos+vec2(-0.225, 0.125), vec2(0.025, 0.1)));
		d = min(d, sdBox(uv, pos-vec2(0.0), vec2(0.25, 0.025)));
		d = min(d, sdBox(uv, pos-vec2(0.0, 0.125), vec2(0.025, 0.35)));
	}
	else if(id == 5)
	{
		d = sdBox(uv, pos+vec2(-0.09, 0.45), vec2(0.115, 0.025));
		d = min(d, sdBox(uv, pos+vec2(-0.225, 0.25), vec2(0.025, 0.225)));
		d = min(d, sdBox(uv, pos-vec2(0.125, 0.0), vec2(0.125, 0.025)));
		d = min(d, sdRing(uv, pos-vec2(0.0, 0.225), -PI*0.75, 0.75, vec2(0.225, 0.05)));
	}
	else if(id == 6)
	{
		d = sdRing(uv, pos+vec2(0.0, 0.225), PI*0.25, 0.25, vec2(0.225, 0.05));
		d = min(d, sdBox(uv, pos-vec2(0.225,-0.02), vec2(0.025, 0.225)));
		d = min(d, sdRing(uv, pos-vec2(0.0, 0.225), 0.0, 1.0, vec2(0.225, 0.05)));
	}
	else if(id == 7)
	{
		d = sdRing(uv, pos+vec2(0.0, 0.225), -PI*0.25, 0.25, vec2(0.225, 0.05));
		d = min(d, sdBox(uv, pos+vec2(0.225,-0.125), vec2(0.025, 0.35)));
	}
	else if(id == 8)
	{
		d = sdRing(uv, pos+vec2(0.0, 0.225), 0.0, 1.0, vec2(0.225, 0.05));
		d = min(d, sdRing(uv, pos-vec2(0.0, 0.225), 0.0, 1.0, vec2(0.225, 0.05)));
	}
	else if(id == 9)
	{
		d = sdRing(uv, pos+vec2(0.0, 0.225), 0.0, 1.0, vec2(0.225, 0.05));
		d = min(d, sdBox(uv, pos+vec2(0.225, 0.0), vec2(0.025, 0.225)));
		d = min(d, sdRing(uv, pos-vec2(0.0, 0.225), -PI*0.725, 0.25, vec2(0.225, 0.05)));
	}

	return smoothstep(0.0, 0.0001+blurval, d);
}

void digitTime(inout vec4 layer, in vec2 uv, in vec2 pos, in float bVal, in vec4 color)
{
	uv -= pos;

	if(abs(uv.x)<2.4 && abs(uv.y)<0.9)
	{
		float mils, secs, mins, hour, blink;
		float num, col;

		mils = fract(iDate.w);
		secs = mod(floor(iDate.w), 60.0);
		secs = (secs<60.0) ? secs : 0.0;
		mins = mod(floor(iDate.w/60.0), 60.0);
		hour = mod(floor(iDate.w/3600.0), 24.0);
		blink = mod(floor(iDate.w*2.0), 2.0);

		// Hours
		num = numbers(uv, vec2(-1.8, 0.0), bVal, int(mod(hour/10.0,10.0)));
		num = min(num, numbers(uv, vec2(-1.2, 0.0), bVal, int(mod(hour,10.0))));
		// Colon
		col = smoothstep(0.0, 0.0001+bVal, sdDisk(vec2(uv.x,abs(uv.y)-0.025), vec2(-0.75,0.1), 0.075));
		num = min(num, (blink<1.0) ? col : mix(0.5,1.0,col));//max(0.5,col));
		// Minutes
		num = min(num, numbers(uv, vec2(-0.3, 0.0), bVal, int(mod(mins/10.0,10.0))));
		num = min(num, numbers(uv, vec2(0.3, 0.0), bVal, int(mod(mins,10.0))));
		// Colon
		col = smoothstep(0.0, 0.0001+bVal, sdDisk(vec2(uv.x,abs(uv.y)-0.025), vec2(0.75,0.1), 0.075));
		num = min(num, (blink<1.0) ? col : mix(0.5,1.0,col));
		// Seconds
		num = min(num, numbers(uv, vec2(1.2, 0.0), bVal, int(mod(secs/10.0,10.0))));
		num = min(num, numbers(uv, vec2(1.8, 0.0), bVal, int(mod(secs,10.0))));

		layer = mix(layer, color, 1.0-num);
	}
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

	fragColor = vec4(0.0);
	vec4 glowLayer = vec4(0.0);

	vec3 col = 0.5 + 0.5*cos(iTime+(uv.xyx*2.0)+vec3(0,2,4));

	digitTime(glowLayer, uv*2.8, vec2(0.0), 0.2, vec4(vec3(col), 1.0));
	digitTime(fragColor, uv*2.8, vec2(0.0), 0.05, vec4(vec3(1.0,0.5,0.3), 1.0));
	fragColor += glowLayer;
}
