void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv.x += fract(iTime);
    uv/=1.+fract(iTime);

    for(int i=1; i<10; i++){
        if(uv.x>.5){
            uv.x = uv.x-.5;
            uv*=2.;
            uv=fract(uv);
        }
    }

    float val = step(.5,length(uv-.5));

    // Output to screen
    fragColor = vec4(vec3(val),1.0);
}
