const int maxI = 128;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.*(2.*fragCoord - iResolution.xy)/iResolution.y;
    vec3 pos = vec3(iMouse.xy / iResolution.y,0.1*iTime);
    vec3 rayVec = normalize(vec3(uv, -1.));
    vec3 rayPos;
    float dist;
    float breakI;
    for(int i; i < maxI; i++)
    {
        dist = texture(iChannel0, 0.05*rayPos - pos).x + 0.05/(length(rayPos) + 0.1) - 0.4;
        if(abs(dist) < 0.01)
        {
            breakI = float(i) / float(maxI);
            break;
        }
        rayPos += rayVec * dist;
    }
    fragColor = texture(iChannel1,0.05*rayPos - pos).xyzz * (16. * breakI / length(rayPos));
}
