void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.y *= iResolution.y / iResolution.x;

    // Mouse input
    vec2 mouseInput = iMouse.xy/iResolution.xy;
    mouseInput.y *= iResolution.y / iResolution.x;

    if (iMouse.xy == vec2(0.0)) mouseInput = vec2(sin(iTime*(sin(iTime*0.25)*0.01))*0.1+0.5, sin(iTime)*0.1+0.25);

    // Time varying pixel color
    vec3 col = vec3(0.0);

    vec2 center = mouseInput;

    vec2 bgScrollUv = vec2(uv.x, uv.y+iTime*0.5)*0.1;
    vec2 bgScrollUv2 = vec2(uv.x * 1.5, uv.y+iTime*0.8)*0.1;

    vec3 bg = texture(iChannel1, bgScrollUv).xyz;    
    vec3 bg2 = texture(iChannel1, bgScrollUv2).xyz;


    col += (bg.x > 0.0 && bg.y > 0.0, bg.z < 0.05) ? bg : vec3(0.0);
    col += (bg.x > 0.0 && bg2.y > 0.0, bg2.z < 0.05) ? bg2 : vec3(0.0);


    float size = 0.1;
    float theta = atan(uv.x - center.x, uv.y - center.y);

    float count = 5.0;
    count /= 2.0;

    float rotation = iTime * 2.0;

    float s = 0.5;
    float r = size * exp(0.5 * sin(2.0 * theta * count + rotation)*s);



    vec2 texPos = vec2(uv.x-center.x,(uv.y-center.y)+iTime)*2.0; 
    vec3 trail = abs(uv.x - center.x) < r && uv.y < center.y ? vec3(texture(iChannel0, texPos).x*2.0)*0.8 : vec3(0.0);

    texPos = vec2(uv.x-center.x+iTime*0.25,(uv.y-center.y)+iTime*10.0)*5.0; 
    trail += abs(uv.x - center.x) < r && uv.y < center.y ? vec3(texture(iChannel0, texPos).x*2.0)*0.2 : vec3(0.0);
    trail = clamp(trail, 0.0, 1.0);



    trail.z = 0.0;

    trail.r += (trail.y > 0.0) ? 1.0 - length(center - uv) * 3.0 : 0.0; 
    trail.g += (trail.y > 0.0) ? 1.0 - length(center - uv) * 4.0 : 0.0;

    trail -= length(uv - center)*2.0;
    trail -= abs(uv.x - center.x)*10.0;

    trail = clamp(trail, 0.0, 1.0);

    col += trail;

    col = (length(uv - center) < r + trail.x*0.1) ? vec3(1.0, length(uv - center)*5.0, 0.0) : col;
    col += (length(uv - center) < r) ? vec3(1.0, 1.0, 0.0) : vec3(0.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
