void mainImage( out vec4 col, in vec2 coord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 res = iResolution.xy,
         r = (2. * coord - res.xy) / max(res.x,res.y);

    float t = iTime,
          d = length(r),
          a = 0.5*atan(r.y,r.x),
          s = 0.04 * (sin(5.*t) + sin(2.*t) + sin(t)),
          o = 0.01 * cos(8.*a+t);

    vec3 b = vec3(cos(6.3*d+s),cos(20.*(d+o)+t),cos(63.*d-t)),
         c = vec3(cos(a),cos(a+2.1),cos(a-2.1));

    b *= b;
    c *= c;

    col.xyz = c * (0.95 - b.x * b.x * b.x) * (1.3 - b.y * b.z);
}
