// variação de https://www.shadertoy.com/view/DdVyzW

#define rot(a) mat2(cos(vec4(0, 33, 11, 0) + a))

void mainImage(out vec4 o, vec2 u) {    
    o -= o;
    vec3 r = iResolution, k, q, 
         p = vec3(sin(iTime * .1), 0, 4. + cos(iTime)),
         D = vec3((u - r.xy / 2.) / r.y, -1);

    float d = 1., dd, t = iTime, j; 
    while(r.z++ < 181.){

        if(fract(sin(iTime*.8)) < .01) d = 1.;

        int j; 
        while(j++ < 5) {
            q = p;
            q.xz *= rot(t + float(j) * 6.28/5.);
            q.xy *= rot(cos(t * 12.) * .03 + 1.);

            k = abs(q);

            while(k.z < k.x || k.z < k.y) 
                k = k.zxy, 
                q = q.zxy;

            dd = length(q.xz - vec2(q.y, 1) * k.z / q.z) * .23 - .02;

            if(dd < d) 
                o.rgb += vec3(
                    j < 2 || j > 3,
                    j < 3 || j > 4,
                    j < 4
                ),
                o.r -= j == 4 && fract(sin(t*2.)) < .3? .85 : 0., // blink
                d = dd;
        } 

        p += D * d;
    }

    o *= .009;    
}
