void mainImage(out vec4 o, vec2 u)
{
    vec2 v = iResolution.xy, w, k = u = .2 * (u + u - v) / v.y;
    o = vec4(1, 2, 3, 0);

    for (float a = .5, t = iTime, i; ++i < 25.; o += (1. + cos(vec4(0, 1, 3, 0) + t)) / length((1. + i * dot(v, v)) * sin(w * 4. - 12. * u.yx + t)))
        v = cos(++t - 8. * u * pow(a += .03, i)) - 6. * u,
        u *= mat2(cos(i + t * .03 - vec4(0, 11, 33, 0))),
        u += .006 * tanh(50. * dot(u, u) * cos(1e2 * u.yx + t))
           + .3 * a * u
           + .004 * cos(t + 5. * exp(-.02 * dot(o, o))),
        w = u / (1. - 3. * dot(u, u));

    o = pow(o = 1. - sqrt(exp(-o * o * o / 2e2)), .3 * o / o) - dot(k -= u, k) / 250.;

    o = vec4(sin(o.xyz + iTime * vec3(.2, .3, .4)) * .5 + .5, 1.0);
}
