float gTime;
vec3 Fractal(vec2 uv, vec2 mn)
{
	vec2 p = gTime * ((iResolution.x-uv)/iResolution.y) - gTime * 0.5 -0.3 - 2.*(smoothstep(0.02, 1.5, gTime)*vec2(.5, .365));
    p.x-=0.3;
    p.y-=0.2;
	vec2 z = p;
	float g = 1., f = 1.0, b=1.;
	for( int i = 0; i < 200; i++ ) 
	{
		float w = float(i)*30.+iTime*2.0;
		vec2 z1 = vec2(2.*cos(w),2.*sin(w));	
        vec2 b1 = vec2(pow(cos(w),.2),pow(sin(w),.2));
		z = vec2( z.x*z.x-z.y*z.y, 2.0 *z.x*z.y ) + p;
		g = min( g, dot(z-z1,z-z1));
		f = min( f, dot(z,z) );
        b = min( b, dot(z-b1,z-b1));
	}
	g =  min(pow(max(1.0-g, 0.0), .15), 1.0);
	b = 1.0+ log(b)/15.;
    vec3 col = vec3(texture(iChannel0, mn*cos(gTime+2.)));
	float c = abs(log(abs(f))/25.0);
	col = mix(col, vec3(f*.04, c*.9, c ), 1.-g);
    col = mix(col, vec3(b*.2,c*.1,c),.1-g);
	return clamp(col, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    gTime = abs(sin(iTime/4.)+cos(iTime/4.))/2.;
    vec2 mn = fragCoord.xy/iResolution.xy ;
    float expand = smoothstep(1.2, 1.6, gTime)*20.0;

	vec3 col = vec3(0.0);
	for (float y = 0.; y < 3.; y++)
	{
		for (float x = 0.; x < 3.; x++)
		{
			col += Fractal(fragCoord.xy + vec2(x, y) * expand , mn*2.  );
		}
	}

	fragColor = vec4(sqrt(col/4.0), 1.0);
}
