#define MAX_STEPS 80

// From tutorial by kishimisu <3
// https://www.youtube.com/watch?v=khblXafu7iA&t=620s


//https://iquilezles.org/articles/palettes/
vec3 palette(float t) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.8, 0.7, 0.5);
    vec3 c = vec3(1.0, 1.0, 0.0);
    vec3 d = vec3 (0.50, 0.20, 0.25);
    return a + b*cos(6.28318*(c*t+d));
}

float sdOctahedron( vec3 p, float s) {
    p = abs(p);
    return (p.x + p.y + p.z - s) * 0.57735027;
}

mat2 rot2D(float angle) {
    float s = sin(angle);
    float c = cos(angle);
    return mat2(c,-s, s, c);
}

float map(vec3 p) {
    p.z += iTime * .4;

    // Space repetition
    p.xy = (fract(p.xy) - .5);
    p.z = mod(p.z, .25) - .125;

    float octahedron = sdOctahedron(p, .15);

    return octahedron;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y;
    vec3 col = vec3(0.0);

    vec3 rO = vec3(0.0, 0.0, -3.0); // Ray Origin
    vec3 rD = normalize(vec3(uv, 1.0)); // Ray direction

    float t = 0.0; // Total distance travelled
    rO.z *= sin(iTime) * 0.7;

    // Raymarching
    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 p = rO + rD * t;

        mat2 screwed = rot2D((t*.2) * sin(iTime) * 0.9);
        p.xy *= screwed;

        float d = map(p);

        t += d;

        if (d < .001 || t > 100.) break;    

        // Coloring
        col = palette(t*.04 + float(i) * .005);
    }



    fragColor = vec4(col,1.0);
}
