mat2 rotate2D(float r){
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec3 tower_pos,tree_pos,pos=vec3(0,.5,-1.2),out_color=vec3(0);
  float dist,tree_scale_acc,tree_scale,tower_scale_acc,tower_scale,fake_wind;
  for(int i;i<100;i++){
    tree_pos=pos+=normalize(vec3((fragCoord.xy*2.-iResolution.xy)/iResolution.y,1.7))*dist;
    tree_pos.xz*=rotate2D(iTime/8.);
    tower_pos=tree_pos/5.;
    tower_pos.y+=3.38;
    tree_scale_acc=5.;
    tower_scale_acc=.7;
    dist=1e9;
    for(int j;j<11;j++){
      tree_scale_acc/=tree_scale=dot(tree_pos,tree_pos);
      tree_pos/=tree_scale+.01;
      tree_pos.y=1.73-tree_pos.y;
      fake_wind=sin(iTime+pos.x+pos.y+pos.z+tree_scale+sin(tower_scale*tower_pos.y+iTime*2.))*.05;
      tree_pos.xz=abs(tree_pos.xz*rotate2D(1.+fake_wind))-.6;
      tower_scale_acc/=tower_scale=min(dot(tower_pos,tower_pos),.5)+.03;
      tower_pos=abs(tower_pos)/tower_scale-.35;
      tower_pos.y-=4.;
      dist=min(dist,max(length(tree_pos.xz)-.02/tree_scale,tree_pos.y)/tree_scale_acc);
    }
    dist=min(dist,(tower_pos.x+tower_pos.z)/tower_scale_acc);
    out_color+=.01/exp(dist*5e3+sin(vec3(2,4,5)-log(tower_scale_acc*tree_scale_acc)*.2)/tower_scale_acc*tree_scale_acc*.3);
   }
  fragColor=vec4(out_color,1);
}
