#define t iTime
#define i0 iChannel0
#define iR iResolution

void mainImage( out vec4 r, in vec2 d )
{

    vec2 uv = d / iR.xy - .5;  
    uv.x *= iR.x / iR.y;

    uv = abs(fract(uv * 2.) - .5);  
    if(uv.x > .5) uv.x = 1. - uv.x;   
    if(uv.y > uv.x) uv = vec2(uv.y, uv.x); 

    float a = t * .7;
    uv *= mat2(cos(a), sin(a), -sin(a), cos(a));//动态旋转

    vec4 tr = texture(i0, uv);

    float g = smoothstep(.0, .5, .5 - length(uv)) * .5;//荧光
    tr.rgb += g * vec3(.3, .6, 1.);//色调

    uv += sin(uv.yx * 10. + t * 2.) * .02;
    r = texture(i0, uv);

    r = mix(r, tr, 1.);
}

vec2 mainSound(int samp, float time)
{
    float loopDuration = 15.0;
    float loopTime = mod(time, loopDuration);  // Time within the loop

    // Define the frequency variable
    float freq = 0.0;

    // Play the melody during the first 14 seconds
    if (loopTime < 1.0) freq = 261.63;  // C4
    else if (loopTime < 2.0) freq = 261.63;  // C4
    else if (loopTime < 3.0) freq = 392.00;  // G4
    else if (loopTime < 4.0) freq = 392.00;  // G4
    else if (loopTime < 5.0) freq = 440.00;  // A4
    else if (loopTime < 6.0) freq = 440.00;  // A4
    else if (loopTime < 7.0) freq = 392.00;  // G4
    else if (loopTime < 8.0) freq = 349.23;  // F4
    else if (loopTime < 9.0) freq = 349.23;  // F4
    else if (loopTime < 10.0) freq = 329.63; // E4
    else if (loopTime < 11.0) freq = 329.63; // E4
    else if (loopTime < 12.0) freq = 293.66; // D4
    else if (loopTime < 13.0) freq = 293.66; // D4
    else if (loopTime < 14.0) freq = 261.63; // C4
    else
    {
        // Silence during the last 1 second (loopTime between 14.0 and 15.0)
        return vec2(0.0);
    }

    // Generate the sound wave with exponential attenuation
    float noteTime = mod(time, 1.0);  // Each note lasts 1 second
    return vec2(sin(6.2831 * freq * noteTime) * exp(-3.0 * noteTime));
}
