// Green screen function from @iq 

// :D :D :D
vec4 compositeGreenScreenTexture(vec2 uv, vec4 background)
{

     vec4 tex = texture(iChannel0, uv);

     float maxrb = max( tex.r, tex.b );
     float k = clamp( (tex.g-maxrb)*5.0, 0.0, 1.0 );

     float ll = length( tex );
     tex.g = min( tex.g, maxrb*0.8 );
     tex = ll*normalize(tex);

     return mix(tex, background, k);

}
const float pi = 3.14159265359;
float rand(vec2 co){ return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // rotate uv
    vec2 rotUV;
    float angle = 50.f / 180.f * pi;
    rotUV.x = cos(angle) * uv.x - sin(angle) * uv.y;
    rotUV.y = sin(angle) * uv.x + cos(angle) * uv.y;

    // Time varying pixel color
    float r = sin(rotUV.y * 25.f);
    float g = sin(rotUV.y * 25.f + iTime);
    float b = sin(rotUV.y * 25.f - iTime);
    vec4 col = vec4(r, g, b, 1.0);

    // Output to screen
    fragColor = compositeGreenScreenTexture(uv, col);
}
